// The input item in n8n
const input = $input.item.json;

// Function to combine emails from markdown and html
const combineEmails = (item) => {
  const markdownEmails = item.markdown.emails || [];
  const htmlEmails = item.html.emails || [];

  // Combine unique emails from both markdown and html
  const combinedEmails = [...new Set([...markdownEmails, ...htmlEmails])];

  // Add a new field with the combined emails
  item.combinedEmails = combinedEmails;

  // Return the modified item
  return item;
};

// Call the function and return the modified input
return combineEmails(input);

const input = $input.all();

// Function to organize emails by websites
const groupEmailsByWebsites = (input) => {
  const emailMap = {};

  // Loop through input items
  input.forEach(item => {
    const email = item.json.combinedEmails; // Access the email from the item
    const url = item.json.url; // Access the URL from the item

    // If the email is already in the map, work with its websites set
    if (emailMap[email]) {
      emailMap[email].websites.add(url);
    } else {
      // Otherwise, initialize a new entry with a Set for unique websites
      emailMap[email] = {
        email: email,
        websites: new Set([url])
      };
    }
  });

  // Convert the map back to an array and websites Set to array
  return Object.values(emailMap).map(item => ({
    email: item.email,
    websites: Array.from(item.websites)
  }));
};

const result = groupEmailsByWebsites(input);

return result;

// 1. Take input text
let str = $input.item.json.html;

// 2. Grab domain name
let toProcess = $('Set domain and callback URL1').item.json.domain;

// 3. Replace dots and dashes into RegExp-friendly format
let transformDots = toProcess.replaceAll('.', '+\\.');
let transformDashes = transformDots.replaceAll('-', '+\\-');

// 4. Create RegExp formats with injected domain
let regularFormat = '([a-zA-Z0-9._-]+@' + transformDashes + ')';
let atSquareBrackets = '([a-zA-Z0-9._-]+\\[at\\]' + transformDashes + ')';
let atSquareBracketsWithSpaces = '([a-zA-Z0-9._-]+\\s\\[at\\]\\s' + transformDashes + ')';

// 5. Combine all formats with conditional expression (OR = '|')
let combined = new RegExp(regularFormat + '|' + atSquareBrackets + '|' + atSquareBracketsWithSpaces, 'gm');

// 6. Find RegExp results in input text
let found = Array.from(str.matchAll(combined)).flat().filter(n => n);
let uniqueValues = [...new Set(found)];

// Return the array of unique emails inside the "output" key
return {
  json: {
    html: {
      emails: uniqueValues
    }
  }
};

