parameters.inputSchema:
{
  "title": "Calendar Agent Input",
  "type": "object",
  "properties": {
    "task": {
      "type": "string",
      "enum": ["create", "readOne", "readAll", "update"],
      "description": "Specifies the type of calendar action to perform. Use 'create' to add a new event, 'readOne' to retrieve a specific event by eventId, 'readAll' to retrieve all events within a time range, or 'update' to edit an existing event."
    },
    "startTime": {
      "type": "string",
      "description": "The start time for the event (if create/update) or the start of the time range (if readAll) in ISO 8601 format (e.g., 2025-01-07T17:27:18)."
    },
    "endTime": {
      "type": "string",
      "description": "The end time for the event (if create/update) or the end of the time range (if readAll) in ISO 8601 format (e.g., 2025-01-07T18:27:18)."
    },
    "title": {
      "type": "string",
      "description": "Title of the event. Typically required when creating a new event."
    },
    "description": {
      "type": "string",
      "description": "Optional description of the event."
    },
    "eventId": {
      "type": "string",
      "description": "Unique identifier of the event. Required when task is 'readOne' (to retrieve a specific event) or 'update' (to edit an existing event)."
    },
    "location": {
      "type": "string",
      "description": "Physical or virtual location for the event (e.g., a conference room or Zoom link)."
    },
    "attendees": {
      "type": "string",
      "description": "A comma-separated list of email addresses to invite to the event. For example: 'john@example.com,jane@example.com'."
    }
  },
  "required": ["task"]
}

