const now = new Date();

// Get UTC values
const utcYear = now.getUTCFullYear();
const utcMonth = now.getUTCMonth(); // Zero-based
const utcDate = now.getUTCDate();
const utcHours = now.getUTCHours();
const utcMinutes = now.getUTCMinutes();
const utcSeconds = now.getUTCSeconds();

// EST offset (UTC-5 during standard time, UTC-4 during daylight saving time)
const isDST = new Date().getTimezoneOffset() < 240; // Eastern time DST starts in March
const estOffset = isDST ? -4 : -5;

const estDate = new Date(Date.UTC(
  utcYear,
  utcMonth,
  utcDate,
  utcHours + estOffset,
  utcMinutes,
  utcSeconds
));

// Format date and time
const formattedDate = estDate.toISOString().split('T')[0];
const formattedTime = estDate.toTimeString().split(' ')[0];

return `The current date and time in EST is ${formattedDate} at ${formattedTime}.`
