parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "action": {
      "type": "string",
      "enum": ["create_event", "update_event", "retrieve_events", "delete_event"],
      "description": "The action to perform on the calendar. Must be one of the predefined options."
    },
    "eventId": {
      "type": "string",
      "description": "The unique identifier of the event. Required for update or delete actions."
    },
    "title": {
      "type": "string",
      "description": "The title of the event. Required for create actions."
    },
    "date": {
      "type": "string",
      "format": "date-time",
      "description": "The date and time of the event in ISO 8601 format. Required for create actions and may be used for updates or retrieval."
    },
    "duration": {
      "type": "string",
      "description": "The duration of the event, e.g., '1h', '30m'. Optional."
    },
    "attendees": {
      "type": "array",
      "items": {
        "type": "string",
        "format": "email",
        "description": "Email address of a attendee."
      },
      "description": "List of participants' email addresses. Optional."
    },
    "location": {
      "type": "string",
      "description": "The location of the event. Optional."
    },
    "description": {
      "type": "string",
      "description": "Additional details about the event. Optional."
    },
    "dateRange": {
      "type": "object",
      "properties": {
        "start": {
          "type": "string",
          "format": "date-time",
          "description": "The start of the date range for retrieving events in ISO 8601 format."
        },
        "end": {
          "type": "string",
          "format": "date-time",
          "description": "The end of the date range for retrieving events in ISO 8601 format."
        }
      },
      "required": ["start", "end"],
      "description": "The date range for retrieving events. Required for retrieve actions if a range is specified."
    }
  },
  "required": ["action"],
  "additionalProperties": false
}

{
  "type": "object",
  "properties": {
    "action": {
      "type": "string",
      "enum": [
        "get_message",
        "get_unread_messages",
        "send_message",
        "mark_message_read",
        "create_label",
        "get_all_labels",
        "get_label",
        "get_draft",
        "get_all_drafts",
        "create_draft"
      ],
      "description": "The action to be performed. Must be one of the predefined options."
    },
    "email": {
      "type": "string",
      "format": "email",
      "description": "The recipient's email address."
    },
    "subject": {
      "type": "string",
      "minLength": 1,
      "maxLength": 255,
      "description": "The subject of the email."
    },
    "message": {
      "type": "string",
      "minLength": 1,
      "description": "The body of the email."
    },
    "limit": {
      "type": "integer",
      "description": "The maximum number of unread messages to retrieve.",
      "minimum": 1,
      "default": 5
    }
  },
  "required": ["action"],
  "additionalProperties": true
}

