// FunÃ§Ã£o para capitalizar o nome
function capitalizeName(fullName) {
    if (typeof fullName === 'undefined') {
        return { fname: '', lname: '', fullname: '' };
    }

    const lowerCaseWords = ['da', 'de', 'do', 'di', 'du'];
    let words = fullName.toLowerCase().split(' ').map((word, index) => {
        if (lowerCaseWords.includes(word) && index !== 0) {
            return word;
        }
        return word.charAt(0).toUpperCase() + word.slice(1);
    });

    let fname = words.shift();
    let lname = words.join(' ');

    // Ajuste para remover o espaÃ§o extra caso nÃ£o haja sobrenome
    let fullname = lname ? `${fname} ${lname}` : fname;

    return { fname, lname, fullname };
}

// FunÃ§Ã£o para formatar email
function formatEmail(email) {
    return email.toLowerCase().trim();
}

// FunÃ§Ã£o para formatar Instagram
function formatInstagram(instagram) {
    // Remover "@" e transformar em letras minÃºsculas
    return instagram.toLowerCase().replace(/^@/, "");
}

// FunÃ§Ã£o para processar e formatar o telefone
function processTelefone(telefoneOriginal) {
    if (!telefoneOriginal) return "";
    const telefoneLimpo = telefoneOriginal.replace(/\D/g, ""); // Remove caracteres nÃ£o numÃ©ricos
    let DDI = '';
    let DDD = '';
    let numero = '';

    if (telefoneLimpo.length > 11) {
        DDI = telefoneLimpo.slice(0, 2);
        DDD = telefoneLimpo.slice(2, 4);
        numero = telefoneLimpo.slice(4);
    } else if (telefoneLimpo.length === 11) {
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else if (telefoneLimpo.length === 10) {
        DDI = '55';
        DDD = telefoneLimpo.slice(0, 2);
        numero = telefoneLimpo.slice(2);
    } else {
        return telefoneLimpo;
    }

    if (DDD && numero) {
        if (parseInt(DDD) > 28 && numero.length === 9 && numero.startsWith("9")) {
            numero = numero.slice(1);
        } else if (DDD <= 28 && numero.length === 8) {
            numero = `9${numero}`;
        }
    }

    return DDI && DDD && numero ? `${DDI}${DDD}${numero}` : "";
}

// FunÃ§Ã£o para extrair o form_name do link manualmente
function extractFormName(link) {
    if (!link) return "";
    const queryStart = link.indexOf('?');
    if (queryStart === -1) return "";

    const queryParams = link.substring(queryStart + 1).split('&');
    for (const param of queryParams) {
        const [key, value] = param.split('=');
        if (key === "form_name") {
            return decodeURIComponent(value);
        }
    }
    return "";
}

