const today = new Date();
const dayOfWeek = today.getDay();
const daysUntilSaturday = (6 - dayOfWeek + 7) % 7;  // 6 representa s√°bado
const nextSaturday = new Date(today);
nextSaturday.setDate(today.getDate() + daysUntilSaturday);

// Retorna um array de objetos no formato que o N8N espera
return [
  {
    json: {
      nextSaturday: nextSaturday.toISOString().split('T')[0],  // Retorna no formato YYYY-MM-DD
    }
  }
];

