function formatarTelefone(telefone) {
  const apenasNumeros = telefone.replace(/[^\d]/g, "");
  const ddi = '55';
  const ddd = apenasNumeros.slice(2, 4);
  const telefone9digitos = apenasNumeros.slice(-9);

  let telefoneFormatado = ddi + ddd + telefone9digitos;
  if (parseInt(ddd, 10) > 28) {
    telefoneFormatado = ddi + ddd + telefone9digitos.slice(-8);
  }

  return telefoneFormatado;
}

// FunÃ§Ã£o para formatar a primeira letra de cada palavra em maiÃºsculo
function primeiraLetraMaiusculaEmTodasPalavras(texto) {
  return texto.split(' ').map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
}

