function calcularDataVencimento(mentoriaVendida, dataDaVenda) {
  const dataVenda = new Date(dataDaVenda);
  const dataAtual = new Date(dataVenda);

  switch (mentoriaVendida) {
    case "Mentoria C-Level":
      // Adiciona 1 ano Ã  data da venda
      dataAtual.setFullYear(dataAtual.getFullYear() + 1);
      break;
    case "Mentoria Modus Inovandi":
      // Adiciona 6 meses Ã  data da venda
      dataAtual.setMonth(dataAtual.getMonth() + 6);
      break;
    case "Mentoria Modus Inovandi Start":
      // Adiciona 4 meses Ã  data da venda
      dataAtual.setMonth(dataAtual.getMonth() + 4);
      break;
    // Adicione mais casos conforme necessÃ¡rio para outras mentorias

    default:
      // Tratamento padrÃ£o para outros casos
      break;
  }

  // Retorna a data de vencimento formatada
  const dia = dataAtual.getDate();
  const mes = dataAtual.getMonth() + 1; // MÃªs Ã© baseado em zero
  const ano = dataAtual.getFullYear();

  // Corrigindo o formato da data para incluir zeros Ã  esquerda
  const diaFormatado = dia < 10 ? '0' + dia : dia;
